function HeroCarousel({ slides }: { slides: HeroImageSlider[] }) {
  const [viewportRef, embla] = useEmblaCarousel({
    skipSnaps: false,
    loop: true,
    containScroll: 'trimSnaps',
  });
  const [nextBtnEnabled, setNextBtnEnabled] = useState(false);

  const scrollNext = useCallback(() => embla && embla.scrollNext(), [embla]);

  const onSelect = useCallback(() => {
    if (!embla) return;
    setNextBtnEnabled(embla.canScrollNext());
  }, [embla]);

  useEffect(() => {
    if (!embla) return;
    onSelect();
    embla.on('select', onSelect);
  }, [embla, onSelect]);

  return (
    <>
      <Box className="embla" position="relative" mx="auto" height="full">
        <Box
          className="embla__viewport"
          ref={viewportRef}
          overflow="hidden"
          width="full"
          height="full"
        >
          <Box
            className="embla__container"
            display="flex"
            userSelect="none"
            height="full"
            marginLeft="-10px"
            sx={{
              KhtmlUserSelect: 'none',
              WebkitTapHighlightColor: 'transparent',
            }}
          >
            {slides?.map((slide, i) => (
              <Box className="embla__slide" key={slide._key} position="relative" minWidth="full">
                <Box
                  className="embla__slide__inner"
                  position="relative"
                  overflow="hidden"
                  height="full"
                >
                  <NextImage
                    className="embla__slide__img"
                    priority={i === 0}
                    placeholder="blur"
                    blurDataURL={slide?.slideImage?.metadata?.lqip}
                    fill
                    style={{ objectFit: 'cover' }}
                    src={urlFor(slide?.slideImage).url()}
                    alt={slide?.location?.title ?? 'Westeros Location'}
                    sizes="80vw"
                    quality={100}
                  />
                  <Heading
                    display={['none', null, null, 'flex']}
                    size="lg"
                    color={slide?.textColor === 'dark' ? 'primaryDark' : 'white'}
                    position="absolute"
                    bottom="5"
                    right="20"
                  >
                    {slide?.location?.title ?? ''}
                  </Heading>
                </Box>
              </Box>
            ))}
          </Box>
        </Box>

        <IconButton
          display={['none', null, null, 'flex']}
          icon={<ChevronRightIcon fill="white" />}
          borderRadius="full"
          colorScheme="whiteAlpha"
          aria-label="Next slide"
          onClick={scrollNext}
          isDisabled={!nextBtnEnabled}
          position="absolute"
          bottom="4"
          right="4"
        />
      </Box>
    </>
  );
}

